import React, { useState } from 'react';

const FileSubmission = () => {
  const [message, setMessage] = useState('');

  const allowedExtensions = [".dat", ".sim", ".yml"];

  function checkValid(fileName) {
    const extension = fileName.substr(fileName.lastIndexOf("."));
    if (allowedExtensions.includes(extension)) {
      alert("Invalid file type. Please select a .dat, .sim, or .yml file.");
      return false;
    }
    return true;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const files = e.target.elements['file'].files;
    const formData = new FormData();
    for (let i = 0; i < files.length; i++) {
      if (!checkValid(files[i].fileName)) {
        return;
      }
      formData.append('files', files[i]);
    }
    try {
      setMessage('Uploading...')
      const response = await fetch('http://localhost:5000/files', {
        method: 'POST',
        body: formData
      });
      const data = await response.json();
      setMessage(data.message);
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div style={{ width: '500px', margin: '0 auto', textAlign: 'center' }}>
      <h1>File submission portal</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" id="file" name="file" onChange={handleFileChange} accept=".dat,.yml,.sim" multiple />
        <input type="submit" value="Upload" />
      </form>
      <div id="message">{message}</div>
    </div>
  );
};

export default FileSubmission;
